mapper.select:
SELECT * FROM travel_info WHERE 1=1
AND hashtag = #{hashtag}
AND addr01 LIKE CONCAT('%',#{gugun},'%')
AND addr01 LIKE CONCAT('%',#{sido},'%')
AND travel_no = #{travel_no}
ORDER BY travel_no DESC

SELECT * FROM notice WHERE 1=1
AND notice_no = #{notice_no}
ORDER BY notice_no DESC

